import React, { useEffect, useState } from 'react';
import { Card, Divider, Layout, Space, Typography } from 'antd';

import './index.css';
import LtList from './lt-list';
import result from "./web-api/get-search-data";

function LtDetail(props) {
  console.log(props.match.params.uid)
  console.log(props.match.uid)
  const data = {content: [{ltName: ''}], totalPages: 1};
  const [ltInfo, setLtInfo] = useState({ltName:'', ltDigest:''});

  const { Title, Paragraph, Text } = Typography;
      
  useEffect(() => {
    console.log(props.match.uid)
    const fetchData = async () => {
        const ltInfo = await result.getSearchData("search/" + props.match.params.uid);
        setLtInfo(ltInfo);
    };

    fetchData();
}, []);

  return (
    <div className="main-page">
      <Space direction="vertical">
        <Space align="start" size="large" >
          <Typography style={{ width: 1000 }}>
            <Title level={3}>{ltInfo.ltName}</Title>
            <Paragraph>{ltInfo.ltDigest}摘要内容
            </Paragraph>
          </Typography>

          <Space direction="vertical">
            <Card style={{ width: 500 }}>
              <Typography>
                <Title level={4}>被引用</Title>
                <Text>拷贝内容xxxxxx拷贝内容xxxxxx拷贝内容xxxxxx拷贝内容xxxxxx拷贝内容xxxxxx</Text>
              </Typography>
            </Card>

            <Card style={{ width: 500 }}>
              <Typography>
                <Title level={4}>参考文献</Title>
                <Text>拷贝内容xxxxxx拷贝内容xxxxxx拷贝内容xxxxxx拷贝内容xxxxxx拷贝内容xxxxxx</Text>
              </Typography>
            </Card>

          </Space>
        </Space>

        <Divider/>
        
        <div>
          <Space direction="vertical">
            <Title level={4}>评论</Title>
            <LtList data={data} pageSize={5}></LtList>
          </Space>
        </div>
        
        <Divider/>
        <div>
          <Space direction="vertical">
            <Title level={4}>相关推荐</Title>
            <LtList data={data} pageSize={5}></LtList>
          </Space>
        </div>
      </Space>
    </div>
  );
}

export default LtDetail;
